mapper.select:

select 

from t_agr_policy_query_router
where routing_query_condition = #{routingQueryCondition,jdbcType=VARCHAR}
  and routing_query_type_cd = #{routingQueryTypeCd,jdbcType=TINYINT}
  and policy_no = #{policyNo,jdbcType=VARCHAR}

mapper.delete:

delete from t_agr_policy_query_router
where routing_query_condition = #{routingQueryCondition,jdbcType=VARCHAR}
  and routing_query_type_cd = #{routingQueryTypeCd,jdbcType=TINYINT}
  and policy_no = #{policyNo,jdbcType=VARCHAR}
